create PROCEDURE [dbo].[pa_RegistrarExcepcion] 
	@IDEXCEPCION uniqueidentifier,
	@NOMBREAPLICACION varchar(60),
	@NOMBRESERVIDOR [varchar](60),
	@NOMBREUSUARIO [varchar](60),
	@NIVELEXCEPCION [varchar](60),
	@CAPA [varchar](60),
	@MENSAJE [varchar](max),
	@STACKTRACE [varchar](max),
	@INNEREXCEPTION [varchar](max)
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
update SOLICITUDCONSTRUCTOR set IDESTADO=@ID_ESTADO where IDSOLICITUDCONTRUCTOR=@ID_SOLICTUDCONSTRUCTOR
select 1 END

